import IDocumentFragment from 'happy-dom/lib/nodes/document-fragment/IDocumentFragment';
import IElement from 'happy-dom/lib/nodes/element/IElement';
/**
 * ShadowRoot.
 */
export default interface IShadowRoot extends IDocumentFragment {
    mode: string;
    innerHTML: string;
    host: IElement;
    /**
     * Clones a node.
     *
     * @override
     * @param [deep=false] "true" to clone deep.
     * @returns Cloned node.
     */
    cloneNode(deep: boolean): IShadowRoot;
}
